      //Se o aluno já reprovou na disciplina por não atingir a nota mínima para recuperação e a nota foi alterada para maior
      if($lDiarioReprovado && $nValorAproveitamento >= $nMinimoParaRecuperacao && $iQuantAprovacaoConselho == 0) {
